      out.write("<script src=\"/resources/js/reply.js\"></script>\n");
      out.write("<script type=\"text/javascript\">\n");
      out.write("$(document).ready(function(){\n");
      out.write("	history.replaceState({},null,null);\n");
      out.write("	\n");
      out.write("	//댓글 페이지 처리\n");
      out.write("	var pageNum=1;\n");
      out.write("	var replyPageFooter=$(\".panel-footer\");\n");
      out.write("	\n");
      out.write("	function showReplyPage(replyCnt){\n");
      out.write("		var endNum=Math.ceil(pageNum/10.0)*10;\n");
      out.write("		var startNum=endNum-9;\n");
      out.write("		\n");
      out.write("		var prev=startNum!=1;\n");
      out.write("		var next=false;\n");
      out.write("		\n");
      out.write("		if(endNum*10>=replyCnt){\n");
      out.write("			endNum=Math.ceil(replyCnt/10.0);\n");
      out.write("		}\n");
      out.write("		if(endNum*10<replyCnt){\n");
      out.write("			next=true;\n");
      out.write("		}\n");
      out.write("		var str=\"<ul class='pagination pull-right'>\";\n");
      out.write("		\n");
      out.write("		if(prev){\n");
      out.write("			str+=\"<li class='page-item'><a class='page-link' href='\"+(startNum-1)+\"'>이전</a></li>\";\n");
      out.write("		}\n");
      out.write("		\n");
      out.write("		for(var i=startNum;i<=endNum;i++){\n");
      out.write("			var active=pageNum==i?\"active\":\"\";\n");
      out.write("			\n");
      out.write("			str+=\"<li class='page-item \"+active+\"'><a class='page-link' href='\"+i+\"'>\"+i+\"</a></li>\";\n");
      out.write("		}\n");
      out.write("		\n");
      out.write("		if(next){\n");
      out.write("			str+=\"<li class='page-item'><a class='page-link' href='\"+(endNum+1)+\"'>다음</a></li>\";\n");
      out.write("		}\n");
      out.write("		\n");
      out.write("		str+=\"</ul>\";\n");
      out.write("		\n");
      out.write("		//replyPageFooter.append(str);\n");
      out.write("		replyPageFooter.html(str);\n");
      out.write("	}\n");
      out.write("	\n");
      out.write("	//댓글 페이지 번호 클릭시\n");
      out.write("	replyPageFooter.on(\"click\",\"li a\",function(e){\n");
      out.write("		e.preventDefault();\n");
      out.write("		console.log(\"page click\");\n");
      out.write("		\n");
      out.write("		var targetPageNum=$(this).attr(\"href\");\n");
      out.write("		console.log(\"targetPageNum:\"+targetPageNum);\n");
      out.write("		\n");
      out.write("		pageNum=targetPageNum;\n");
      out.write("		\n");
      out.write("		showList(pageNum);		\n");
      out.write("	});\n");
      out.write("	\n");
      out.write("	//댓글 조회,추가,수정,삭제\n");
      out.write("	var bnoValue='");
      out.write((java.lang.String) org.apache.jasper.runtime.PageContextImpl.proprietaryEvaluate("${board.bno}", java.lang.String.class, (javax.servlet.jsp.PageContext)_jspx_page_context, null));
      out.write("';\n");
      out.write("	var replyUL=$(\".chat\");\n");
      out.write("	\n");
      out.write("	showList(1);\n");
      out.write("	\n");
      out.write("	//댓글 전체 조회\n");
      out.write("	function showList(page){\n");
      out.write("		\n");
      out.write("		replyService.getList({bno:bnoValue,page:page},				\n");
      out.write("			function(replyCnt,list){\n");
      out.write("				console.log(\"댓글 전체 갯수:\"+replyCnt);\n");
      out.write("				console.log(\"댓글 목록:\"+list);\n");
      out.write("				\n");
      out.write("				if(page==-1){\n");
      out.write("					pageNum=Math.ceil(replyCnt/10.0);\n");
      out.write("					showList(pageNum);\n");
      out.write("					return;\n");
      out.write("				}\n");
      out.write("							\n");
      out.write("				if(list==null || list.length==0){\n");
      out.write("					replyUL.html(\"\");\n");
      out.write("					\n");
      out.write("					return;\n");
      out.write("				}\n");
      out.write("				\n");
      out.write("				var str=\"\";			\n");
      out.write("				var len=list.length || 0;\n");
      out.write("				\n");
      out.write("				for(var i=0;i<len;i++){\n");
      out.write("					console.log(list[i]);\n");
      out.write("					\n");
      out.write("					str+=\"<li class='left clearfix' data-rno='\"+list[i].rno+\"'>\";\n");
      out.write("					str+=\"	<div>\";\n");
      out.write("					str+=\"		<div class='header'>\";\n");
      out.write("					str+=\"			<strong class='primary-font'>\"+list[i].replyer+\"</strong>\";\n");
      out.write("					str+=\"			<small class='pull-right text-muted'>\"+replyService.displayTime(list[i].replyDate)+\"</small>\";\n");
      out.write("					str+=\"		</div>\";\n");
      out.write("					str+=\"		<p>\"+list[i].reply+\"</p>\";\n");
      out.write("					str+=\"	</div>\";\n");
      out.write("					str+=\"</li>\";\n");
